CREATE FUNCTION IsTypeOf(
	@entityID as int,
	@typeID as int
) returns bit as
	begin
	if @entityID IS NULL OR @typeID IS NULL
		return 0;
	if @typeID = 1
		return 1;

	DECLARE @entityTypeID as int = NULL;

	while 1=1
		begin
		SELECT @entityTypeID = TypeID FROM dbo.Entity WHERE EntityID = @entityID;
		if @entityTypeID IS NULL OR @entityTypeID < @typeID
			return 0;

		if @entityTypeID = @typeID
			return 1;
		SET @entityID = @entityTypeID;
		end
	
	return 0;
	end
GO

CREATE FUNCTION dbo.GetSummary(
	@content as nvarchar(max),
	@default as nvarchar(255) = NULL
) returns nvarchar(255) as
	begin
	declare @summary nvarchar(255) = cast(@content as XML).query('//span[@viak="summary"]').value('.', 'nvarchar(255)');
	if len(@summary) = 0
		return @default;
	if ascii(@summary) = 10
		set @summary = trim(substring(@summary, 2, len(@summary) - 2));
	if substring(@summary, len(@summary), 1) = '.'
		set @summary = trim(substring(@summary, 1, len(@summary) - 1));
	return @summary;
	end
GO


