	PRINT dbo.GetTabsHtml(1, N'765 Новые, 999999 Средние, 146191 Старые');
	PRINT dbo.GetTabsHtml(2, N'765 Новые, 999999 Средние, 146191 Старые');
*/
CREATE FUNCTION dbo.GetTabsHtml(
	@tabNumber int, -- active tab number
	@tabTitles nvarchar(1024) -- comma-delimited list of space-delimited pairs of "live journal numeric id" and "tab title"
) returns nvarchar(4000) as
	begin
	declare @html nvarchar(4000) = N'';
	declare @tabPair nvarchar(300);
	declare @tabLiveJournalId nvarchar(12);
	declare @tabTitle nvarchar(256);
	declare @tabCount int = 0;
	declare @tabPos int = 0;
	declare @linkFormat nvarchar(4000) = N'<a href="http://viakviak.livejournal.com/%s.html" target="_blank">%s</a>';
	declare @selectedFormat nvarchar(4000) = N'<b>%s</b>';
	declare dbCursor CURSOR FOR
		SELECT trim(value) as TabTitle FROM string_split(@tabTitles, ',');

	OPEN dbCursor;
	WHILE 1=1
		begin
		FETCH NEXT FROM dbCursor INTO @tabPair;
		if @@FETCH_STATUS < 0 break;
		set @tabCount = @tabCount + 1;

		set @tabPair = trim(@tabPair);
		set @tabPos = charindex(N' ', @tabPair);
		set @tabLiveJournalId = trim(substring(@tabPair, 1, @tabPos));
		set @tabTitle = trim(substring(@tabPair, @tabPos, len(@tabPair)));

		if len(@html) > 0
			set @html = @html + N' | ';
		if @tabNumber = @tabCount
			set @html = @html + formatmessage(@selectedFormat, @tabTitle);
		else
			set @html = @html + formatmessage(@linkFormat, @tabLiveJournalId, @tabTitle);

		end

	set @html = '<br/>' + @html + '<br/>';

	CLOSE dbCursor;
	DEALLOCATE dbCursor;
	return @html;
	end
GO

