Title OR dbo.GetSummary(Content, Title)
*/

CREATE PROCEDURE nav.GetContent (@cutoffLineNumber int = 0, @tabNumber int, @liveJournalIdFrom int, @liveJournalIdTo int) as
	DECLARE @articleCount as int;
	-- declare @chunkSize int = 50000; -- 65000 -- 80000
	-- declare @liveJournalIdFrom int = (@tabNumber - 1) * @chunkSize + 1;
	-- declare @liveJournalIdTo int = @tabNumber * @chunkSize;
	declare @ArticlesRef as nvarchar(2048);

	if @tabNumber = 5
		set @liveJournalIdTo = 999999

	begin try
		set @ArticlesRef = dbo.GetTabsHtml(@tabNumber, N'198084 Допотопные, 146191 Древние, 245954 Старые, 399989 Давние, 400272 Недавние, 765 Последние'); -- , 346059 Новые, Неолит, Мезолит, Палеолит; Недавние

		SELECT @articleCount = COUNT(*) FROM dbo.Article;

		SELECT -99 as LineNumber, 0 as EntityID, 0 as LiveJournalID, N'Это интересно!' as Title, N'<b>Это интересно!</b><br/>' as Reference, '' as 'Url', '' as SummaryReference, '' as Summary
		UNION ALL
		SELECT	(-1) * cast(ROW_NUMBER() OVER (ORDER BY LiveJournalID DESC) as int) as LineNumber, a.EntityID, LiveJournalID, Title,
				dbo.GetLink(LiveJournalID, Title) as Reference,
				dbo.GetArticleUrl(LiveJournalID) as 'Url',
				dbo.GetLink(LiveJournalID, dbo.GetSummary(Content, Title)) as SummaryReference,
				dbo.GetSummary(a.Content, null) as Summary
		FROM	dbo.Article a INNER JOIN
				dbo.ArticleLabel al ON al.ArticleID = a.EntityID INNER JOIN
				dbo.Label l ON l.EntityID = al.LabelID
		WHERE	l.LabelName IN (N'welcome')
		UNION ALL
		SELECT 0 as LineNumber, 0 as EntityID, 0 as LiveJournalID, N'Статьи' as Title, @ArticlesRef as Reference, '' as 'Url', @ArticlesRef as SummaryReference, '' as Summary
		UNION ALL
		SELECT	cast(ROW_NUMBER() OVER (ORDER BY LiveJournalID DESC) as int) as LineNumber,
				EntityID, LiveJournalID, Title,
				IIF(@cutoffLineNumber = ROW_NUMBER() OVER (ORDER BY LiveJournalID DESC), N'<lj-cut>', N'') +
					dbo.GetLink(LiveJournalID, Title) as Reference,
				dbo.GetArticleUrl(LiveJournalID) as 'Url',
				IIF(@cutoffLineNumber = ROW_NUMBER() OVER (ORDER BY LiveJournalID DESC), N'<lj-cut>', N'') +
					dbo.GetLink(LiveJournalID, dbo.GetSummary(Content, Title)) as SummaryReference,
				dbo.GetSummary(Content, null) as Summary
		FROM	dbo.Article
		WHERE	LiveJournalID BETWEEN @liveJournalIdFrom AND @liveJournalIdTo
		UNION ALL
		SELECT cast((SELECT COUNT(*) FROM dbo.Article) + 1 as int) as LineNumber,
				0 as EntityID, 0 as LiveJournalID, N'' as Title, N'</lj-cut>' as Reference, '' as 'Url', '' as SummaryReference, '' as Summary
		WHERE	@cutoffLineNumber > 0 AND @articleCount >= @cutoffLineNumber
		ORDER BY LineNumber;
	end try
	begin catch
		PRINT 'nav.GetContent failed';
		SELECT	ERROR_NUMBER() AS ErrorNumber  
				,ERROR_SEVERITY() AS ErrorSeverity  
				,ERROR_STATE() AS ErrorState  
				,ERROR_PROCEDURE() AS ErrorProcedure  
				,ERROR_LINE() AS ErrorLine  
				,ERROR_MESSAGE() AS ErrorMessage;
	end catch;
GO

