CREATE PROCEDURE nav.GetInvalidXmlArticles as
	DECLARE @lineNumber int;
	declare @liveJournalID int;
	declare @title as nvarchar(256);
	declare @content as nvarchar(max);
	declare @summary as nvarchar(256);

	declare dbCursor CURSOR FOR 
	SELECT	cast(ROW_NUMBER() OVER (ORDER BY LiveJournalID DESC) as int) as LineNumber,
			LiveJournalID, Title, a.Content as Content
	FROM	dbo.Article a
	OPEN dbCursor;
	WHILE 1=1
	begin
		FETCH NEXT FROM dbCursor INTO @lineNumber, @liveJournalID, @title, @content;
		if @@FETCH_STATUS < 0 break;
		begin try
			SET @summary = cast(@content as XML).query('//span[@viak="summary"]').value('.', 'nvarchar(255)');
			--PRINT cast(@lineNumber as nvarchar) + '. ' + cast(@liveJournalID as nvarchar) + '. ' + @title;
		end try
		begin catch
			PRINT '*** ' + cast(@lineNumber as nvarchar) + '. ' + cast(@liveJournalID as nvarchar) + '. ' + @title;
		end catch
	end
	CLOSE dbCursor;
	DEALLOCATE dbCursor;
GO

